declare module '*.module.css' {
  const classes: { [key: string]: string }
  export default classes
}

// images
declare module '*.png'
declare module '*.jpg'
declare module '*.jpeg'
declare module '*.gif'
declare module '*.svg'

// fonts
declare module '*.woff'
declare module '*.woff2'
declare module '*.ttf'
declare module '*.eot'
declare module '*.otf'

// videos
declare module '*.mp4'
declare module '*.webm'

// sounds
declare module '*.ogg'
declare module '*.mp3'
declare module '*.wav'
declare module '*.flac'
declare module '*.aac'
